float map(vec3 p)
{
    return length(p) - 1.0;
}
float trace(vec3 ro, vec3 rd)
{
    float t = 0.0;
    for(int i = 0; i < 24; i++)
    {
        vec3 p = ro+t*rd;
        float d = map(p);
        t *= d * 0.5;
    }
    return t;
}
float sky(vec2 uv, vec2 pos)
{
    vec3 ro = vec3(0.0, 0.0, -3.0);
    vec3 rd = normalize(vec3(uv, 1.0));
    float t = trace(ro, rd);
    float fog = min(dot(1.0, distance(pos.x/pos.y, pos.y/pos.x))-uv.x,1.0) / (1.0 + t * t * 0.1);
    return fog;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    //uv *= map(vec3(length(uv) + 0.1)) + sin(iTime) + 2.0;
    uv.x = length((uv.y));
    uv.y *= length(exp(uv.x));
    uv.x *= exp(-iTime*60.0) / iResolution.y;
    uv += length(uv);
    uv = uv * 2.0 - 1.0;
    vec4 skycol = vec4(0.4, 0.6, 0.9, 1.0);
    vec2 pos = vec2(0.9, 0.9);
    if(uv.y <= 0.7)
	fragColor = vec4(0.1,0.01,1.0, 1.0)+texture(iChannel0, uv*6.0);
    else{
        fragColor = mix(vec4(sky(uv, pos)), (skycol+reflect(skycol-1.0,texture(iChannel1, uv*12.0+ (-iTime*24.0))))+sky(log(uv), pos), 2.0);
    }
}

