//FabriceNeyret2 version (123 chars)

void mainImage(out vec4 C, vec2 f)
{
	C -=C;
    for (float i=0.;i<16.;i++) {
        vec2 v = fract(i*f/iResolution.y);
        C = v.x<.2 && v.y<.2 ? 1.-C : C; 
    }
}


