#define M_PI 3.1415926535897932384626433832795

float gradientCircle(vec2 uv, vec2 pos, float radius,float width) {
    return clamp((length(uv - pos)-radius)/width,-1.0,1.0);
}

float pattern1D(float x, float f, float size) {
  return sin(clamp(2.0 * sin(x / size * M_PI) + 3.0 * f,-1.0,1.0)*M_PI/2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 scale = iResolution.xx;
	vec2 uv = fragCoord.xy / scale;
    vec2 mouse = iMouse.xy / scale;

    float gradient = gradientCircle(uv,mouse,0.2,mouse.y * 0.2);

    vec4 tex1 = texture(iChannel0,uv*4.0);
    vec4 tex2 = texture(iChannel1,uv*1.0);

    float size = mouse.x * 0.02;

    float pattern = (pattern1D(uv.x,gradient,size) + pattern1D(uv.y,gradient,size))/2.0;
  	fragColor = mix(tex2,tex1,pattern/2.0+0.5);

    //Color-coded
    //fragColor *= mix(vec4(0.5,1.0,1.0,1.0),vec4(1.0,1.0,0.5,1.0),gradient/2.0+0.5);
}
