void mainImage(out vec4 color, in vec2 pixCoords)
{
    float radius = iResolution.x / 12.0;
    float waveLevel1 = 100.0;
    float waveLength1 = 20.0;
    float waveWidth1 = 15.0;
    float waveLevel2 = 120.0;
    float waveLength2 = 15.0;
    float waveWidth2 = 10.0;

    float lSunCenterX = 0.5*iResolution.x;
    float lSunCenterY = 0.7*iResolution.y;

    float PI = 3.14;
    float radToDeg = 180.0 / PI;
    float lTan = sqrt(pow(lSunCenterX - pixCoords.x, 2.0) + pow(lSunCenterY - pixCoords.y, 2.0));
    float lDeltaX = lSunCenterX - pixCoords.x;
    float lDeltaY = lSunCenterY - pixCoords.y;
    float lAngle = atan(lDeltaX, lDeltaY) * radToDeg;
    float lDeltaRay = 30.0;

    if (lTan < radius)
    {
       	color = vec4(1.0, 1.0, 0.0, 1.0);
    }
    else if (pixCoords.y < waveLevel1 + waveLength1 * (cos(iTime * 2.0 + pixCoords.x / waveWidth1)))
    {
        color = vec4(0.0, 0.5, 0.8, 1.0);
    } 
    else if (pixCoords.y < waveLevel2 + waveLength2 * (cos(iTime * 2.0 + pixCoords.x / waveWidth2)))
    {
        color = vec4(0.0, 0.1, 0.8, 1.0);
    } 
    else if ((mod(lAngle,20.0) <= 10.0 ) && (lTan > radius + 20.0) && (mod(lSunCenterX - lTan,25.0) < 15.0))
    {
        color = vec4(0.9, 0.9, 0.0, 1.0);
    }
    else
    {
        color = vec4(0.8, 0.8, 0.9, 1.0);
    }  
}
