void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.x;
    vec2 p = vec2 (.5,.28) - uv;
    vec3 col = vec3 (uv.x,uv.y,1.0);
    col *= smoothstep (.0,.35,length (p));
    col = fract (col*4.0);
    vec3 cou = vec3 (cos(fract (col)*90.0));
	fragColor = vec4(cou*2.0,1.0);
}
