#define time iTime
#define resolution iResolution.xy
#define mouse (iMouse.xy/iResolution.xy)
#define R(x) (fract(x)<.5?floor(x):ceil(x))
vec4 logo(vec2 u) {
	u-=.5;
	u.x*=resolution.x/resolution.y;
	vec3 c = vec3(0);
	float d = length(u);
	d=sqrt(d);
	float q = 0.;
	float v = abs(d-.5);
	v=1.-pow(v,1./2.);
	float k = v*(dot(normalize(u),-vec2(1,-1.2))*(v*v));
	c.x+=k;c.y+=k/1.4;
	c=max(c,0.);
	float cu =length(u);
	if (cu<.25) {
		q=1.;
		c+=vec3(.6, .3,0.)*(.4-pow(cu,3.));
	}
	float x = abs(sin(u.y*23.)/10.-u.x);
	if (x<.025&&u.y<.15&&u.y>-.15) {
		float u = x/.025;
		c=vec3(1.-R(u*u))/1.;
		q=1.;
	}
	return vec4(c-vec3(.2),q);
}
float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}

void mainImage( out vec4 fragColor, vec2 fragCoord ) {
	float t = time;
	vec2 oa = vec2(t,.2+t);
	vec2 ob = vec2(-t,t*.5);
	vec2 uv = ( fragCoord.xy / resolution.xy*5. ) ;
	float a = noise(uv+oa), b = noise(uv+ob);
	float c = 1.-pow(abs(b-a),1./5.);
	vec4 q =  vec4( c/5., c/1.9, c/1., 0.);
	vec4 p =  vec4( c/1.9, c/5., 0., 0.);
	vec4 f =  vec4( c/3.9, 0., c/1., 0.);
	vec4 o = vec4(0);
	if (uv.x<.333*5.) {
		o=vec4(0,.5,.9,0);
	}else
	if (uv.x<.666*5.) {
		o=vec4(1,0,0,0);
	}else
	if (uv.x<5.) {
		o=vec4(.5,0,1,0);
	}
	vec4 clo = q;
	if (mouse.x>.333) {
		clo=p;

	}
	if (mouse.x>.666) {
		clo=f;	
	}
	if (uv.y<2.5) {

		if (uv.x<.333*5.) {
			clo=q;
		}else
		if (uv.x<.666*5.) {
			clo=p;
		}else
		if (uv.x<5.) {
			clo=f;
		}	
	}
	if (uv.y<.1) clo=o;
	vec4 lg = logo(vec2(uv.x,uv.y-3.7));
	fragColor = clamp(lg,0.,1.)+clo*(1.-lg.w);

}
