void mainImage( out vec4 color, in vec2 pixCoords )
{
    color = vec4(0.0,0.5,1.0,1.0);

    float centerX = iResolution.x / 2.0 + 275.0;
    float centerY = iResolution.y / 2.0 + 125.0;
    float radius = iResolution.x / 12.0; 

    float amplitude = 25.0 ;
    float period = 90.0 ;
    float phi = 120.0 ;

    float distance = sqrt(pow(pixCoords.x - centerX, 2.0) + pow(pixCoords.y - centerY, 2.0)) ; 

    if ( distance < radius ) 
    	{
     		color = vec4(1.0,0.4,0.0,1.0);
       	}

    if ( pixCoords.y < cos ( pixCoords.x / period + iTime) * amplitude + phi )
    	{
     		color = vec4(0.0,0.0,0.6,1.0);
       	}

     if ( pixCoords.y < cos ( pixCoords.x / period - 2.0 * iTime) * amplitude + phi - 15.0 ) 
    	{
     		color = vec4(0.0,0.0,0.8,1.0);
       	}

     if ( pixCoords.y < cos ( pixCoords.x / period + 3.0 * iTime) * amplitude + phi - 30.0 ) 
    	{
    		color = vec4(0.0,0.0,1.0,1.0);
       	}

}
