void mainImage( out vec4 tc, in vec2 fragCoord )
{
vec2 uv = (fragCoord/iResolution.xy)*vec2(1.777,1.0)- vec2(0.3885,0.0);
int L = 10;
float M = ceil(uv.x)*(ceil(1.0-uv.x));
float TC = (1.0-uv.y)+min(uv.x,1.0-uv.x);
float t = 2.0;
int IT =int(mix(0.0,float(L),fract(iTime*0.2)));

for(int i=0; i<IT; i++)
{
TC += min(fract(uv.x*t),fract(1.0-fract(uv.x*t)))/t;
t *=2.0;
}
TC = floor(TC);

    // Output to screen
    tc = vec4(TC,TC,TC,1.0);
}
