void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float PEAKS = 3.0;
    float BAND = 2.0 + sin(0.5 * iTime);

	float mx = max(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / mx;
    vec2 center = iResolution.xy / mx * 0.5;
    vec2 p = uv - center;

    float ang = atan(p.y, p.x);
    float d = distance(uv, center);
    float band0 = floor(d * BAND);
    float band1 = 1.0 + band0;
    float band2 = 1.0 + band1;

    float wav1 = cos(ang * PEAKS * band1 * band1);
    float wav2 = cos(ang * PEAKS * band2 * band2);
    float wavT = (d - band0 / BAND) * BAND;
    float wav = 0.1 * mix(wav1, wav2, clamp(wavT, 0.0, 1.0));

    float r = sin(-iTime + 100.0 * d * (1.0 + wav));
    float g = -r;
    float b = cos(iTime * 3.0 + 100.0 * d);

    fragColor = vec4(r, g, b, 1.0);
}
