#define S(v,r)    smoothstep(3./R.y,0.,  length( v )-r )
#define disk(P,r) S( P-U , r )

float line(vec2 p, vec2 a,vec2 b) { // https://www.shadertoy.com/view/4dcfW8
    p -= a, b -= a;
	float h = clamp(dot(p, b) / dot(b, b), 0., 1.);   // proj coord on line
	return length(p - b * h);                         // dist to segment
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 C = vec2(.1,.3),            // box center
         S = vec2(.8,.3),            // scale (i.e. radiis )
         R = iResolution.xy,
         U = (u+u-R) / R.y,

    // --- bounding box     
    B = ( U - C ) / S;              // box coordinates [-1,1]x[-1,1]   
    B = abs(B);
    O = .8*vec4( max( B.x, B.y ) < 1.); 
  //O = vec4( B.x < 1. && B.y < 1.); // this form might stop earlier
    O += disk(C,.04);

    // --- show pseudo distance field
    O += .1+.1*sin(50.*max( B.x, B.y ));

    vec2 M = (2.*iMouse.xy-R) / R.y;
    O.bg += disk(M,.04);
    B = ( M - C ) / S;
    float d =  max( abs(B).x, abs(B).y );
    O.g += disk( C + S*B/d ,.05);
    O.r += disk( C - S*B/d ,.05);
    O.g += S(line(U,M,C - S*B/d), 0.); 

    // --- show ray front/back intersection
/**
