     d = min(K.x,K.y); O.g += .5*disk( M+d*D, .05);
    K = abs(( S +C-M ) / D); d = min(K.x,K.y); O.r += disk( M+d*D, .05);
     d = max(K.x,K.y); O.r += .5*disk( M+d*D, .05);
