float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float clamps(in float val){
    return clamp(val,0.,1.);
}

float sin1(float val){
    return clamp(sin(val)*2.,-1.,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime;
    vec4 texture1 = texture(iChannel0,uv);
    vec4 texture2 = texture(iChannel1,uv);
    float random = rand(uv);
    float blend = clamps((sin1(time)/2.)+0.5);
    if(random > blend){
    blend = floor(blend);
    }
    if(random < blend){
    blend = ceil(blend);
    }
    vec4 colors = mix(texture1,texture2,blend);
	fragColor = vec4(colors);
}
