float radius = 0.3;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord -.5* iResolution.xy ) / iResolution.y;
    float dist = distance(vec2(0), uv);

    if (dist < radius) {
     	float c = smoothstep(radius, radius - ((sin(iTime) + 1.0) / 2.0) - 0.1, dist);    
		fragColor = vec4(c, 0, 0, 1);

    } else {
        fragColor = vec4(0);
    }



}
