void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv - vec2(.5);

    float m = float(int(iTime*1000.)%1200);
    m = m/10.;
    float x0 = -uv.x*m;
    float y0 = -uv.y*m;

    float gray = abs(pow(x0-round(x0),2.)+pow(y0-round(y0),2.));
	fragColor = vec4(gray,gray,gray,1.);
}
