void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Patrick Gregg

    //Sets pixel size of each square
    float boardSize = 10.0;

    //Sets each block of boardSize pixels aside. 
    vec2 a = floor(fragCoord / boardSize);

    //Lists the pixels out as either 1 or 0 alternating every boardSize number of pixels.
    float board = mod(a.x + a.y, 2.0);

    //Sets the color of each pixel to match the board.
	fragColor = board * vec4(1.0,1.0,1.0,1.0);
}
