#define PI 3.14159

float circle(vec2 pos, float rad)
{
    return length(pos) - rad;
}

float box(vec2 pos, vec2 b)
{
  vec2 d = abs(pos) - b;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

// polynomial smooth min (k = 0.1);
// https://iquilezles.org/articles/smin
float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// This is rough and ready but it works for now
// https://www.topcoder.com/community/data-science/data-science-tutorials/geometry-concepts-line-intersection-and-its-applications/#!
vec2 line_line(vec2 s1, vec2 e1, vec2 s2, vec2 e2)
{
    float a1 = e1.y - s1.y;
    float b1 = s1.x - e1.x;
    float c1 = a1 * s1.x + b1 * s1.y;

    float a2 = e2.y - s2.y;
    float b2 = s2.x - e2.x;
    float c2 = a2 * s2.x + b2 * s2.y;

	float det = a1*b2 - a2*b1;
    if(det == 0.0)
    {
        // LINES ARE PARALLEL - SHOULD NEVER HAPPEN
        return s1;
    }
    else
    {
        return vec2((b2*c1 - b1*c2) / det, (a1*c2 - a2*c1) / det);
    }
}

vec2 getMouse()
{
    return iMouse.xy/iResolution.y;
}

float smush(vec2 uv)
{
    const float k = 0.01;
    return smin(
        smin(
            circle(uv - vec2(0.7, 0.5), 0.1),
            box(uv - vec2(0.5, 0.5), vec2(0.2, 0.1)),
            k
        ),
        circle(uv - getMouse(), 0.1),
        k
    );
}

float orbit(vec2 uv)
{
    const float k = 0.1;
    const vec2 pos = vec2(0.75, 0.5);
    float dist = 0.1 * (2.0 + sin(iTime));
    float ang = iTime / (2.0 * PI);
    return smin(
        circle(uv - pos, 0.0),
        circle(uv - pos - dist * vec2(cos(ang), sin(ang)), 0.0),
        k);
}

