const float sides = 6.0;
const float seg = 2.0 * PI / sides;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord / iResolution.y);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Distance field lookup
    float d = dist(uv);
    vec2 n = normalize(vec2(dFdx(d), dFdy(d)));

    // Calculate angle towards distance field surface (0-2PI)
    float ang = atan(n.y, n.x) + PI;

    // Work out the angle at which the segment starts and the angle from segment start to uv
    float seg_ang = floor(ang / seg) * seg;
    float rel_ang = abs(ang - seg_ang);

    // Compute vectors for each edge of the segment
    vec2 s0 = vec2(cos(seg_ang), sin(seg_ang));
    vec2 s1 = vec2(cos(seg_ang + seg), sin(seg_ang + seg));

    // Intersect the distance field normal with the edge of a unit hexagon (s1 - s0) to get a distance adjustment factor
    float adjust = length(line_line(n, n + (s1 - s0), vec2(0), s1));

    // Adjust towards the hexagon edge - draws hexagons around the distance field for a circle
    float val = d * pow(adjust, 0.5 + 0.5 * sin(iTime));

    val = sin((val + iTime * 0.1) * 50.0) > 0.0 ? 1.0 : 0.0;

    // Uncomment to silhouette the shapes
    if (d < 0.0) val = 0.0;

    // Output to screen
    fragColor = vec4(val * col,1.0);
}
