#define intensity 0.4

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 target = vec4(1.,0.,0.,1.);
    vec4 color = texture(iChannel0,uv);

    vec3 diff = color.xyz - target.xyz;
	float luminance = dot(color, vec4(0.299, 0.587, 0.114, 0.));
 	vec4 bgColor = vec4(luminance, luminance, luminance, 1.0);

    if(dot(diff,diff) < intensity) {

        bgColor = color;

    }

    fragColor = bgColor;
}
