#define PI = 3.1415926589

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r = 2.0*vec2(fragCoord.xy - .5*iResolution.xy) / iResolution.y;

    vec3 pixel = vec3(0.0);
    vec2 c = vec2(0.0,0.0);
    vec2 d = r-c;

    vec3 color = vec3(2.0,2.0,0.0);

   	float val1;
    float val2;

    float radius1 = 0.8;
    float radius2 = 0.65; //try to change :)
    float pow_val = 0.8;  //try to change :)

    float p1 = length(d/vec2(radius1));
    float p2 = length(d/vec2(radius2));

	if(length(d) < radius1)
    {
        val1 = 1. - clamp(p1, 0.0, 1.0);
    }

    if(length(d) < radius2)
    {
          val2 = 1. - clamp(p2, 0.0, 1.0);
          val2 = pow(val2,pow_val);

    }


    pixel = vec3(val1-val2) * color;

    fragColor = vec4(pixel,1.0);
}
