const float feathering = 10., blend = 0.;

float map(vec2 a, float density) {
    vec2 f = fract(a); a -= f; f = f*f*(3.-2.*f);
    vec4 r = fract(sin(vec4(a.x + a.y*1e3) + vec4(0, 1, 1e3, 1001)) * 1e3)*feathering*density;
    return clamp(mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y)-feathering, blend, 1.-blend);
}

void mainImage( out vec4 c, in vec2 w ) {
	vec2 p = w*8./iResolution.xy - floor(iTime)*4.5;
	c.rgb =  mix(vec3(.2,.3,.1), mix(vec3(.4,.3,.1), vec3(.7,.6,.4), map(p, 2.)), map(p-2.5, 2.5));
}
