vec3 dots(vec2 coords) {
	return abs(vec3(sin(coords.x) * cos(coords.y), cos(coords.x) * sin(coords.y), 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;
    float scale = 50.0;
    float offset = -iMouse.x / (iResolution.x * 0.5) + iTime;
    vec3 col = dots(uv * scale + (iTime + sin(offset + uv.x *  10.0))) * min(sin(offset + uv.x *  10.0) + 1.5, 1.0);
	fragColor = vec4(col,1.0);
}
