void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    float speed = 3.;
    float bounceAmount = .5;
    float bounceOffset = 2.5; // in Radians

    float time = iTime * speed;


	vec2 uv = ( fragCoord / iResolution.xy ) - .5;

    uv.x = abs( uv.x );

    float xVal = (uv.x - .5);
    float yVal = uv.y - .5;


    float bounce = bounceAmount * sin( time + bounceOffset );
    float valSin = sin( uv.x * ( 20. - 4.* sin( time )) - time + bounce);
    float valStraight = uv.x;

    float totalFlapAmount =  ( uv.x * 4. + .5);

    float flapCycle = ( ( valSin * .06) + ( valStraight ) * .6 *  sin( time ));



   	uv.y += totalFlapAmount * flapCycle;


    vec4 col = vec4( 1. - abs(uv.y *40. * ( 1. + uv.x * 20. ) ));

    col *= smoothstep( uv.x , .2 , .6 );

	fragColor = col; //vec4( col ,1.0);

}
