void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5,0.5);
    float dist =  sin(sqrt(dot(uv, uv))+sin(iTime*1.0))*1.5;

    //if ( (dist > (0.1+0.05)) || (dist < (0.1-0.1)) ){
        float aux = (3.1415)/16.0;
        float angl = atan(uv.x, uv.y)*iTime*0.3;
        if (mod(angl/aux +0.5, 2.) < 1. )
            fragColor = vec4(texture(iChannel1, uv).rgb,0);

        else if ( (dist > (0.2+sin((0.15+uv.x)*iTime)*0.5)) || (dist < (0.2-sin((0.15+uv.y)*iTime)*0.2)) )
     		fragColor = vec4(texture(iChannel2, uv).rgb,0);

    //}
    else 
       fragColor = vec4(texture(iChannel0, uv).rgb,0);

}
