void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;  

    vec2 p = uv -0.5;
    p.x *= iResolution.x / iResolution.y;


    vec4 col = texture(iChannel0, uv); 

    p.y += sin(p.x * 5. + iTime * 0.1) * 0.05;
    p.x += sin(p.y * 10. + iTime * 0.5) * 0.25;

    col = col * 0.975 + vec4(smoothstep(0., 0.01, length(p) - 0.15)) * 0.025;

    col.gb *= 0.9;
    col.r += 0.004;

    fragColor = col;
}
