const vec3 colorA = vec3(0.8, 0.3, 0.5);
const vec3 colorB = vec3(0.1, 0.4, 0.8);
const vec3 colorC = vec3(0.3, 0.8, 0.5);

float checker(vec2 coord) {
    coord = mod(floor(coord), 2.0);
    return mod(coord.x + coord.y, 2.0);
}

void mainImage(out vec4 color, vec2 coord) {
    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = coord * ps;
    color = vec4(0.0);

    vec3 cam = vec3(uv * 2.0 - 1.0, 1.0);

    vec2 cam_uv = cam.xz / cam.y * 2.0;
    cam_uv.y -= iTime;

    float ground = uv.y < 0.5 ? checker(cam_uv) : 0.0;

    color.rgb += mix(colorA, colorB, ground * (1.0 - uv.y) * 0.75);

    float vignette = smoothstep(0.5, 1.0, distance(uv, vec2(0.5)));

    color.rgb = mix(color.rgb, colorC, vignette);
}
