void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0,uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 aa = texture(iChannel1,uv);
    vec2 raysource =vec2(sin(iTime*0.5)*0.25+0.35,0.89);
    float rayamount = 0.0f;
    for(float i = 0.; i < 1.0;i+=0.01){
        if(length(texture(iChannel1,mix(uv,raysource,i)))>1.9){

        rayamount += 0.003/pow(distance(mix(uv,raysource,i),raysource)+1.0,2.0);
        }
    }
    aa+=rayamount;
  //  if(length(aa)>1.9){
    //        aa.xyz = vec3(1.0/(distance(uv,raysource)+1.0));
      //      }
    fragColor = vec4(rayamount)+aa;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0,uv);
}
