/**  // version webGL1 - compatible

void mainImage( out vec4 O, vec2 U )
{
	vec2 R = iResolution.xy;
    U = (U+U-R)/R.y;
    float t = .1*(iTime-9.9), r = 1., c,s;

    O -= O;
    for( int i=0; i< 99; i++)
	    U *= mat2(c=cos(t),s=sin(t),-s,c),
        r /= abs(c) + abs(s),
        O = smoothstep(3./R.y, 0., max(abs(U.x),abs(U.y)) - r) - O;
}

