void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x = uv.x * 1.5;


    float scaledTime = iTime*0.1;
    float warpMaxDist = 10.0;


    vec4 warpCoord0;
    warpCoord0 = texture(iChannel1, vec2(uv.x+scaledTime*2.0, uv.y)*2.0);

    vec4 warpCoord1;
    warpCoord1 = texture(iChannel1, vec2(uv.x-scaledTime, uv.y+0.5));

    vec4 warpCoord = warpCoord0*0.5 + warpCoord1*0.5;

    warpCoord = warpCoord / 255.0 * warpMaxDist;

    vec4 outColor;
    outColor = texture (iChannel0,
                       vec2( uv.x + sin(warpCoord.x),
                             uv.y + cos(warpCoord.y) )
                       );



	fragColor = outColor;
}
