void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float  valueX = step(0.0 + (sin(iTime) + 1.0)* 0.45 ,uv.x) - step(0.1 + (sin(iTime)+1.0)* 0.45,uv.x);
    float  valueY = step(0.0 + (sin(iTime) + 1.0)* 0.45 ,uv.y) - step(0.1 + (sin(iTime)+1.0)* 0.45,uv.y);
	//float  valueY = step(abs(sin(iTime +11.0)),uv.y);
	fragColor = vec4(valueX * sin(iTime),valueY,0.45,0.5);

