float hash( float n ) {
    return fract(sin(n)*43758.5453123);
}

float noise( in vec2 x ) {
    vec2 p = floor(x);
    vec2 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0;

    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x), mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
}

float fbm(in vec2 p ) {
    return 0.5*noise(p) + 0.25*noise(2.0*p)+0.125*noise(4.0*p)+0.0625*noise(8.0*p)
        +0.03125*noise(16.0*p)+0.015625*noise(32.0*p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 * 2.0 * uv;
    p+=fbm(4.0*p+vec2(iTime, 0));
    p = p * 4.0;
    float f1 = fbm(p), f2=fbm(p + 100.0), f3=fbm(p + 200.0);
	fragColor = vec4(0.3, f2, 0.9*f3,1.0);
}
