// quick blend test for kryz clouds shader
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

   	// uv directional scroll
    vec2 _dir = vec2(1.0,0.35);
    vec2 uv1  = uv + _dir*iTime*0.059;
    vec2 uv2  = uv + _dir*iTime*0.05;    

    //Get the texture color
    vec4 t1 = texture(iChannel0, uv1);
    vec4 t2 = texture(iChannel1, uv2);

    // show raw blend value as black/green when mouse button is pressed...
    if (iMouse.z>0.5)
    {    
	    t1 = vec4(0.0,0.0,0.0,1.0);
	    t2 = vec4(0.0,1.0,0.0,1.0);
    }


    vec2 size = vec2(sin(iTime*0.12)*5.5, sin(iTime*0.27)*1.2);
//    vec2 size = vec2(2.0-sin(iTime*0.5)*4.0, 2.0);
    vec2 mask = vec2(0.5 * sin(size.x * uv.x - iTime*0.25) *0.5, 0.5 * cos(size.y * uv.y - iTime*0.25) + 0.5);

    float blend = (mask.x+mask.y)*0.5;
    blend = clamp(blend+0.1,0.1,0.9);
    fragColor = mix(t1,t2,blend);
}
