float insideBox(vec2 v, vec2 bottomLeft, vec2 topRight) {
    vec2 s = step(bottomLeft, v) - step(topRight, v);
    return s.x * s.y;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float xTime = abs(cos(iTime * .1)), yTime = abs(sin(iTime * .1));
    float xSpace = xTime * iResolution.x, ySpace = yTime * iResolution.y;
    int boxSize = 50;

    vec2 xy = fragCoord.xy / iResolution.xy,
        uv = vec2(/*Input X*/ xTime, /*Input Y*/ yTime);
    vec4 textColor = texture(iChannel0, xy),
        dotColor = texture(/*texture*/ iChannel0, uv);

    if(1.0 == insideBox(fragCoord.xy, vec2(int(xSpace)-boxSize/2, int(ySpace)-boxSize/2),
                                   	vec2(int(xSpace)+boxSize/2, int(ySpace)+boxSize/2)))
		textColor = dotColor;
    /*output color*/ fragColor = textColor;
}
