vec4 voro(vec2 uv) // https://www.shadertoy.com/view/ltK3WD
{
	uv.x = abs(uv.x);

	float a = atan(uv.x, uv.y) * 2.;
	float r = length(uv);

	uv = vec2(a,r);

	vec2 g = floor(uv);
	vec2 f = fract(uv);
	vec2 p,cp;
	float t = iDate.w * 0.1;
	float d = 1.,k;;
	for(int x=-2;x<=2;x++)
    for(int y=-2;y<=2;y++)
    {	
        p = vec2(x,y);
		p += .5 + .35*sin( t * 10. + 9. * fract(sin((g+p)*mat2(2,5,5,2)))) - f;
        k = max(abs(p.x)*.866 - p.y*.5, p.y);
        if (k<d)
        {
            d = k;
            cp = p;
        }
	}

	vec3 n = vec3(0);

    if ( d == -cp.x*.866 - cp.y*.5) 	n = vec3(1,0,0);
	if ( d == cp.x*.866 - cp.y*.5) 		n = vec3(0,1,0);
	if ( d == cp.y) 					n = vec3(0,0,1);

    return vec4(sqrt(texture(iChannel0, -n).rgb*d*1.5),d);
}

void mainImage( out vec4 f, vec2 v )  // https://www.shadertoy.com/view/4dd3Ds
{
    f.xyz = iResolution;

	for (int i = 0; i < 60; i++) 
        f.z += length((v-f.xy*.5)*f.z/f.y + sin(f.z - 4.*iDate.w + vec2(1.6,0))) - 2. 
        - voro((v+v-f.xy)/f.y).x;

    f = vec4(1,2,3,1) / (2. + .2*f*f).z;
}
