void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 texel = texture( iChannel0, uv );

    // Alt.
	//#define EPSILON 1./255.
    //if ((texel.r < EPSILON) && (texel.g < EPSILON) && (texel.b < EPSILON))
	if ((texel.r == 0.) && (texel.g == 0.) && (texel.b == 0.))
	    texel.rgb = vec3( 1.0 ); // custom processing

    fragColor = texel;
}
