void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (-iResolution.xy + 2.0 * fragCoord)/iResolution.y;

    float r = length(uv);

    float t = 25.0;

    float time = iTime;

    float a[3];

    float c = 0.0;

    for (int i = 0; i < 3; i++)
    {

    float s = r;

    float tuv = uv.x;
    uv.x = uv.x - uv.y;
    uv.y = uv.y +tuv;

    s *= (sin(abs(uv.x) * t * 0.2));

    s *= (sin(abs(uv.y) * t * 0.2));

    float m = mod(s + (time + c) * 0.2, 0.5);

    //m *= mod(r - iTime * 0.2, 0.5);

    a[i] = m;    

    c += sin(iTime);

    }

    vec3 col = vec3(a[0], a[1], a[2]);

    fragColor = vec4(col, 1.0);
}
