//based on https://www.shadertoy.com/view/4sc3DB

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 //   vec2 uv = (fragCoord.xy/iResolution.xy);
    vec2 uv = 0.999*(fragCoord.xy/iResolution.xy)+0.001;

    if (iMouse.w < 1.) {      
        if (length(vec2(sin(iTime)*.75, cos(iTime*1.1294)*.75)-(uv*2.-1.)) < .03) {
                    fragColor = vec4(1.,.9,1.,1.);
        return;
        }
    } else {
    if (length(iMouse.xy/iResolution.xy-uv) < .03) {
        fragColor = vec4(1.,0.,1.,1.);
        return;
    }
    }

    vec4 c = texture(iChannel0, uv)*5.;

    vec2 odr = 0.75/iResolution.xy;

    vec4 cLeft = texture(iChannel0, uv-vec2(odr.x,0.)),
         cRight = texture(iChannel0, uv+vec2(odr.x,0.)),
         cUp = texture(iChannel0, uv-vec2(0.,odr.y)),
         cDown = texture(iChannel0, uv+vec2(0.,odr.y));

    c += cLeft.wyzx*(abs(cos(iTime+uv.x*32.234+cRight.w*32.234))+1.);
    c += cRight.zxyw*(abs(cos(uv.x*32.234+cLeft.z*32.34+iTime*1.36))+1.);
    c += cUp*(abs(cos(iTime*2.12+uv.y*32.1432+cDown.y*32.24))+1.);
    c += cDown.wzyx*(abs(cos(uv.y*32.345+cUp.x*32.234))+1.);
       c=(c*c)/3.0;

    fragColor = max(c/11.6-.0001, 0.);
}
