void mainImage(out vec4 color, in vec2 coord){
    vec2 res = iResolution.xy;
    float aspect = res.x / res.y;
	vec2 uv = ((coord.xy / res) - 0.5);
    uv.x *= aspect;
    float t = iTime * -0.25;
    vec2 rot = uv * mat2(cos(t), -sin(t), sin(t), cos(t));
    rot.x /= aspect;
    rot += 0.5;
    rot.x = clamp(rot.x, 0.0, 1.0);
    rot.y = clamp(rot.y, 0.0, 1.0);
    color = texture(iChannel0, rot);
}
