//distance field
float df(vec3 p) {
    return length(p) - 1.0;
}

//calculate normal
vec3 calcNormal(vec3 p) {
	float eps = 0.001;
	return normalize(vec3(
		df(p + vec3(eps, 0, 0)) - df(p + vec3(-eps, 0, 0)),
		df(p + vec3(0, eps, 0)) - df(p + vec3(0, -eps, 0)),
		df(p + vec3(0, 0, eps)) - df(p + vec3(0, 0, -eps))
		));
}


struct Ray {
    vec3 position;
    int steps;
    float t;
};

//it finds the intersection point and returns various data
const int maxSteps = 10;
Ray trace(vec3 from, vec3 rayDir) {
    vec3 p;
    int steps = 0;
    float t = 0.0;
    for(int i = 0; i < maxSteps; i++) {
        p = from + t*rayDir;
        float d = df(p);
        if(d < 0.001) {
            steps = i;
            break;
        }
        t += d;
    }
    return Ray(p, steps, t);
}


//main
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //(-1, -1) ~ (1, 1)
	vec2 uv = (2.0*fragCoord.xy - iResolution.xy)/iResolution.y;

    //camera settings
    vec3 camPos = vec3(0, 0, -5);
    vec3 camFront = vec3(0, 0, 1);
    vec3 camUp = vec3(0, 1, 0);
    vec3 camRight = cross(camUp, camFront);
    float focus = 1.0;

    //ray direction
    vec3 rayDir = uv.x*camRight + uv.y*camUp + focus*camFront;

    //trace
    Ray tr = trace(camPos, rayDir);

    //coloring
    vec3 color = vec3(0);
    if(tr.t < 6.0) {
        color = -calcNormal(tr.position);
    }

	fragColor = vec4(color, 1.0);
}
