#define N 10.

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
	U /= R;
    U = 1.5*U-.25;

    O -= O; 
    for (int j=0; j<int(N); j++)
        for (int i=0; i<int(N); i++) {
            vec2 T = texture(iChannel0, (vec2(i,j)+.5)/R).xy, // piece #(i,j)
                 S = step(abs(U-T), vec2(.5/N));                // S==1 if project here
            if (S.x*S.y>0.) { O = texture(iChannel1, U-T+.5/N+vec2(i,j)/N); return; }
        }
}

#define N 10.

#define prevM texture(iChannel0, vec2(N+.5)/R)
#define rnd(U) fract(4567.*sin(.3+dot(U,vec2(1,17.43))))

void mainImage( out vec4 O, vec2 U )
{
    if (iFrame==0) {
        U -= .5;
        float n = mod( (U.x+N*U.y)*17.+floor(iDate.w), N*N); // pieces permutation (17: any big prime)
        U = .5 + vec2(mod(n,N), floor(n/N)) + .5*(2.*rnd(U)-1.);

        O = vec4((U-.5)/N+.5/N,0,0);          // initial location of pieces
        O.xy = 1.4*O.xy-.2;
        return;
    }

    vec2 R = iResolution.xy,
        M = iMouse.xy/R, D=M-iMouse.zw/R;
    M = 1.5*M-.25;
    D = 1.5*D-.25;

    O = texture(iChannel0,U/R);

    if ( U == vec2(N+.5) )                    // memorize mouse details
        if (iMouse.z<=0.) O -= O;             // no click
        else {
            O.xy = D;                         // mouse delta
            if (prevM.xy==vec2(0))            // onClick
     		  for (int j=0; j<int(N); j++)                     // seek for the piece under mouse
       			 for (int i=0; i<int(N); i++) {
            		vec2 T = texture(iChannel0, (vec2(i,j)+.5)/R).xy, // piece #(i,j)
                 	     S = step(abs(M-T), vec2(.5/N));                // S==1 if project here
                    if (S.x*S.y>0.) { O.zw = vec2(i,j)+.5; return; }    // memorize active piece
                }
        }

    if (max(U.x,U.y)>N || iMouse.z <= 0.) return;

    if (U == prevM.zw)                  // active piece
        O.xy += D-prevM.xy;             // displace like mouse
}












