//Ethan Alexander Shulman 2016


//units = pixels
#define RETICLE_SIZE 100.
#define BAR_DISTANCE 40.
#define INDICATOR_BAR_THICKNESS 7.
#define INDICATOR_BAR_FADE 8.
#define PROGRESS_BAR_THICKNESS 10.
#define TOTAL_SIZE (RETICLE_SIZE + BAR_DISTANCE + INDICATOR_BAR_THICKNESS*2.0 + PROGRESS_BAR_THICKNESS*2.0)




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord*2. - iResolution.xy);
    float len = length(uv),
          ang = 3.14-atan(uv.y,uv.x);

    float indicatorOn = mod(floor(iTime),2.),
          progress = cos(iTime)*.5+.5;

    float blen = (RETICLE_SIZE+BAR_DISTANCE)-len;

    float d = max(RETICLE_SIZE-len,
                  max(indicatorOn*(INDICATOR_BAR_THICKNESS-abs(blen))/INDICATOR_BAR_FADE,
                     clamp(ang/(-6.28)+progress*2.,0.,1.)*(PROGRESS_BAR_THICKNESS-abs(blen+INDICATOR_BAR_THICKNESS+PROGRESS_BAR_THICKNESS))));
	fragColor = vec4(clamp(pow(max(0.,d/4.),1./1.3),0.,1.));
}
