bool check(float coord, vec2 bound)
{
    return (coord > bound.x) && (coord < bound.y);
}

void mainImage( out vec4 color, in vec2 coord )
{
    vec2 pos = coord / iResolution.xy ; 
    vec2 bound = vec2(1./4.,3./4.);
    if(check(pos.x, bound) && check(pos.y,bound) )
    {
        color = vec4((pos - 0.25) * 2., .5 + sin(iTime) , 1.);
    }
    else 
    {
        color = vec4(0.,0.,0.,1.);
    }
}
