//Ethan Alexander Shulman 2016

#define EXTENT 1
#define SEED .842
#define PI 3.14


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    if (iFrame < 120) {
        //2 seconds to go fullscreen

        //start with texture
    	fragColor = texture(iChannel1, fract(abs(uv/6.+cos(vec2(iDate.w)/vec2(.9283,.38938))*3.0)));
        return;
    }

    vec4 c = texture(iChannel0, uv),
         o = c;
    float sum = 1.;

    for (int x = -EXTENT; x < (EXTENT+1); x++) {
        for (int y = -EXTENT; y < (EXTENT+1); y++) {
            if (x == 0 && y == 0) continue;

            vec4 s = texture(iChannel0, fract(uv+vec2(x,y)/iResolution.xy)),
                 as = s*PI*2.-PI,
                 ao = o*PI*2.-PI,
                 so = sin(ao/SEED)*SEED,
                 cs = cos(as/SEED)*SEED;


            float a = max(0.,sin(dot(as*mat4(so,cs.yzwx,so.zwxy,cs.wxyz),
                              ao*mat4(cs,so.yzwx,cs.zwxy,so.wxyz))));

            o += (s+sin(so)*.01*cos(SEED))*a;
            sum += a;
        }
    }

    fragColor = o/sum;
}
