void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragCoord += 2.*sin(fragCoord.yx*.01+iTime);

    fragCoord += 3.*sin(iTime*94.*vec2(3434,253));
    if(!bool((iFrame+int(fragCoord.x))%10) )
    	for(int i = 0; i < 4; i++)
 	   		fragColor[i] = texture(iChannel0,(fragCoord+2.*float(i))/iResolution.xy)[i];
    else
        discard;
}

mat2 rot(float a){
    float s = sin(a), c = cos(a);
    return mat2(c,s,-s,c);
}


void mainImage( out vec4 C, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    if(!bool(iFrame%20))
    {
        C = vec4(1);
        if(abs(uv.x-.5) > 0.45 || abs(uv.y-.5) > .45)
        {
            C = vec4(0.);
        }
        uv = abs(uv-.5);
		//uv -= .5;
        float a = .1*sin(iTime);
        float b = .1*sin(iTime*.546);

        uv *= rot(a);
        uv /= .9+b;

        uv += .5;

        //C *= .5+.5*sin(vec4(1,2,3,4)*iTime*84.+vec4(34,23,562,0));

        C *= (1.-texture(iChannel0,uv))*1.5;

    }
    else
        C = texture(iChannel0,uv);

}
