void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    float x = U.x/R.x, y = U.y/R.y,     // normalized coordinates
          t = 0., v = 1.;

    if (y > .66)                        // Correct case (top)
        O = vec4 ( x-t++ < .5 ? 0. : ( v =  t , v ) );
      //O = vec4 ( x-t++ < .5 ? 0. : ( t = -2., v ) ); // correct too

    else if (y > .65) O = vec4(1,0,0,0);// (red separator)

    else if (y > .33)                   // Bug 1 (mid): v = t evaluated too early on windows/Angle
        O = vec4 ( x-t++ < .5 ? 0. : ( v = t, v<.5 ? 0. : 1. ) );

    else if (y > .32) O = vec4(1,0,0,0);// (red separator)

    else                                // Bug 2 (bottom): t = -2. evaluated too early on windows/Angle
        O = vec4 ( x-t++ < .5 ? 0. : ( t = -2., v<.5 ? 0. : 1. ) );


}
