float map(vec2 p)
{
    mat2 scale = mat2(.2, 1., .01, 2.);
    p = pow(p, vec2(8)) * scale;
    vec2 z = p;

    float opacity = .8;
    for (float i = 1.; i < 4.; i++)
    {
        float a = 6. * atan(z.x, z.y), r = length(z);
        z = vec2(cos(a), sin(a)) * r + p;

        if (r > 1.)
            return min(opacity, i);
    }
    return 0.;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 R = iResolution.xy;
    vec2 p = (fragCoord * 2. - R) / min(R.x, R.y);
    fragColor = (1. - map(p)) * texture(iChannel0, (fragCoord / iResolution.xy));
}
