#define D  .005 / abs( length( mod(U,d+d) -d ) -d.x )

void mainImage(out vec4 O, vec2 u) {
    for ( vec2 R = iResolution.xy,
               U = 2.*(u+u-R)/R.y,
               d = vec2(.58,1);
          O.a++ < 5.;
          O += D, U += d*.5, O += D   )
        U.x += d.x; }



