void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/(iResolution.xy);
    //ganger en værdi på for at se hvor hurtigt eller langsomt den ændre sig
    vec2 k = vec2 ((-1.78*pow(uv.x,2.0)+1.78*uv.x+0.56),(-1.78*pow(uv.y,2.0)+1.78*uv.y+0.56));
    //farven gradueres opløftet i et sted mellem 0 og 1 for at ændre    
    // x eller y fortæller fra hvilken kant man graduerer farve
    fragColor = vec4(0.0, k.x*k.y, 0.0, 1.0); 
    //green eller 1-green fortæller hvorfra den viser, de fire positioner viser RGB og gennemsigtighed
}
