void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;

    xy -= vec2(0.1337);
    float angle = sin(iTime * 0.3) * 3.14;
    xy = mat2(cos(angle),asin(angle), sin(angle),cos(angle)) * xy;
    xy += vec2(0.5);

    vec4 texColor = texture(iChannel0,xy);
    texColor.r *= abs(sin(iTime) * 5.5 - xy.x);
    texColor.g *= abs(cos(iTime) * 9.5);
    texColor.b *= abs(sin(iTime) * 13.37);
    fragColor = texColor;
}
