void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 col = vec4(uv,0.5+0.5*sin(iTime),1.0);

    float rval = texture(iChannel0,fragCoord / iResolution.xy).y;


    fragColor = 1.3*clamp(rval*(col+vec4(0.3,0.3,0.3,1.0)), vec4(0.0,0.0,0.0,0.0), vec4(1.0,1.0,1.0,1.0));
}

float read(vec2 fragCoord, vec2 pos)
{
    return texture(iChannel0,(fragCoord+pos)/iResolution.xy).y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor=texture(iChannel0, fragCoord/iResolution.xy); 
    vec2 uv = fragCoord/iResolution.xy;
    if(iFrame<30 || mod(float(iFrame),3000.0) ==0.0)
    {
        float val = (texture(iChannel1,(fragCoord)/iResolution.xy)).x;

        fragColor = vec4(1.0,1.0,1.0,0.0)*val;
        if(val<0.33)
           fragColor = vec4(0.0,0.0,1.0,0.0);
        if(val>0.33 && val<0.66)
           fragColor = vec4(0.0,1.0,0.0,0.0);
        if(val>0.66)
            fragColor = vec4(1.0,0.0,0.0,0.0);
    }
    else
    {

        float clock = (texture(iChannel2,(fragCoord)/iResolution.xy)).x;
		if(clock!=0.0)
            return;

        float curval = read(fragCoord, vec2(0.0,0.0));
        if(fragColor.x == 1.0)
        {
            float zadd = 0.0;
            float compare = abs(cos(iTime)+1.0);
            zadd+=read(fragCoord, vec2(-1.0,-1.0));
            zadd+=read(fragCoord, vec2(0.0,-1.0));
            zadd+=read(fragCoord, vec2(1.0,-1.0));
            zadd+=read(fragCoord, vec2(-1.0,0.0));
            zadd+=read(fragCoord, vec2(1.0,0.0));
            zadd+=read(fragCoord, vec2(-1.0,1.0));
            zadd+=read(fragCoord, vec2(0.0,1.0));
            zadd+=read(fragCoord, vec2(1.0,1.0));
            if(zadd>compare)
                fragColor = vec4(0.0,1.0,0.0,0.0);
        }
        else
		if(fragColor.y==1.0)
            fragColor = vec4(0.0,0.0,1.0,0.0);
        else            
       	 if(fragColor.z==1.0)
            fragColor = vec4(0.0,0.0,0.0,1.0);
        else            
        if(fragColor.w==1.0)
            fragColor = vec4(1.0,0.0,0.0,0.0);  

        if(fragColor == vec4(0.0,0.0,0.0,0.0))
           fragColor.x=1.0;
    }
}

vec4 read(vec2 fragCoord, vec2 pos)
{
    return texture(iChannel0,(fragCoord+pos)/iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 val = read(fragCoord, vec2(0.0,0.0));   

    fragColor = val + vec4(0.5,0.5,0.5,0.5);
        if(val.x>=1.0) fragColor = vec4(0.0,0.0,0.0,0.0); 

}
