#define RADIUS 0.5
#define AB_SCALE 1.5

bool diskColor(in vec2 uv, vec2 offset)
{
    uv = uv + 0.25 - length(texture(iChannel0, (uv - (iTime / 10.0)) + offset)) * 0.25;

    float d = length(uv) - RADIUS;
    if(d < 0.0001) {
        return true;
    }
    return false;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (-iResolution.xy + 2.0 * fragCoord) / iResolution.y;

    vec3 color = vec3(1);
    if(diskColor(uv, vec2(0.03, 0) * AB_SCALE)) {
        color *= vec3(1, 0, 0);
    }
    if(diskColor(uv, vec2(0, 0.03) * AB_SCALE)) {
        color *= vec3(1, 1, 0);
    }
    if(diskColor(uv, vec2(0.02, 0.02) * AB_SCALE)) {
        color *= vec3(0, 0, 1);
    }
    if(diskColor(uv, vec2(0.0))) {
        color *= vec3(0);
    }

    fragColor = vec4(color, 1.0);
}
